SELECT qnum, LPAD(' ', (LEVEL-1)*3, ' ') || title title, author, qdate, content, hit, anum FROM queBoard START WITH anum=0 CONNECT BY PRIOR qnum=anum ORDER SIBLINGS BY qnum DESC
SELECT * FROM( SELECT q.qnum, q.title, q.author, q.qdate AS qdate, q.content, q.hit, attsize, attname, attid FROM queBoard q LEFT OUTER JOIN attach a ON q.qnum = a.qnum ORDER BY q.qnum DESC, attid ASC )t WHERE qnum=#{qnum}
SELECT * FROM queBoard WHERE qnum=#{qnum}
UPDATE queBoard SET hit =hit+1 WHERE qnum= #{qnum}
INSERT INTO queBoard(qnum, title, author, qdate, content, hit, anum) VALUES(QUEBOARD_SEQ.NEXTVAL, #{title}, #{author}, sysdate, #{content}, #{hit}, #{anum} )
INSERT INTO attach (attid, attsize, attname, qnum, anum) SELECT ATTACH_SEQ.NEXTVAL AS attid, t.* FROM (
SELECT #{item.attsize} AS attsize, #{item.attname} AS attname, (SELECT MAX(qnum) FROM queBoard) AS qnum, (SELECT MAX(anum) FROM queBoard) AS anum FROM DUAL
)t
INSERT INTO attach (attid, attsize, attname, qnum, anum) SELECT ATTACH_SEQ.NEXTVAL AS attid, t.* FROM (
SELECT #{item.attsize} AS attsize, #{item.attname} AS attname, #{item.qnum} AS qnum, (SELECT MAX(anum) FROM queBoard) AS anum FROM DUAL
)t
UPDATE queBoard SET title=#{title}, content=#{content} WHERE qnum=#{qnum}
DELETE FROM attach WHERE qnum=#{qnum}
DELETE FROM queBoard WHERE qnum=#{qnum}
DELETE FROM attach WHERE attid=#{attid}
SELECT * FROM attach WHERE qnum=#{qnum}
