mapper.update:
INSERT INTO addgoods_att(goodspho_seq,goodsnum,mainpic_original,mainpic_server,detail_original,detail_server) SELECT addgoods_att_SEQ.NEXTVAL AS goodspho_seq, t.* FROM (
SELECT (SELECT MAX(goodsnum)FROM goods) AS goodsnum, #{item.mainpic_original}, #{item.mainpic_server},#{item.detail_original},#{item.detail_server} FROM DUAL
)t

mapper.select:
SELECT * 
FROM shop
WHERE ordernum = #{ordernum, jdbcType=VARCHAR}
